import React from 'react';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
import { CheckCircle2 } from 'lucide-react';

const SuccessAlert = ({ message, createdAt, onClose }) => {
  React.useEffect(() => {
    const timer = setTimeout(() => {
      onClose();
    }, 3000); // 3초 후 자동으로 닫힘

    return () => clearTimeout(timer);
  }, [onClose]);

  return (
    <Alert className="fixed top-4 right-4 w-96 bg-white border-green-500 animate-in fade-in slide-in-from-top-2 z-50">
      <CheckCircle2 className="h-5 w-5 text-green-500" />
      <AlertTitle className="text-green-700 font-semibold">등록 완료!</AlertTitle>
      <AlertDescription className="mt-2">
        <p className="text-gray-700">{message}</p>
        <p className="text-sm text-gray-500 mt-1">등록 시간: {createdAt}</p>
      </AlertDescription>
    </Alert>
  );
};

export default SuccessAlert;
