import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

function UseCouponPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const couponCode = new URLSearchParams(location.search).get('code');

  const handleUseCoupon = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:8000/api/use-coupon', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ coupon_code: couponCode }),
      });

      const data = await response.json();

      if (response.ok) {
        setMessage(data.message);
      } else {
        setMessage(data.detail || '쿠폰 사용 중 오류가 발생했습니다.');
      }
    } catch (error) {
      setMessage('서버 연결 중 오류가 발생했습니다.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="use-coupon-page">
      <h2>쿠폰 사용하기</h2>
      {couponCode ? (
        <>
          <p>쿠폰 코드: {couponCode}</p>
          <button onClick={handleUseCoupon} disabled={isLoading}>
            {isLoading ? '처리 중...' : '쿠폰 사용하기'}
          </button>
          {message && <p>{message}</p>}
        </>
      ) : (
        <p>유효한 쿠폰 코드가 없습니다.</p>
      )}
      <button onClick={() => navigate('/')}>메인 페이지로 돌아가기</button>
    </div>
  );
}

export default UseCouponPage;
